    public function detectDBFieldByColumnName($column_name)
    {
        $column_name = strtolower(trim($column_name));
        $db_field = '';
        switch ($column_name) {
            case 'sku':
                $db_field = 'sku';
                break;
            case 'product_name':
            case 'product name':
                $db_field = 'name';
                break;
            case 'tax_rate':
            case 'tax rate':
                $db_field = 'taxrate';
                break;
            case 'unit_price':
            case 'unit price':
                $db_field = 'unit_price';
                break;
            case 'per_unit':
            case 'per unit':
                $db_field = 'per_unit';
                break;
            case 'unit_per_price':
            case 'unit per price':
                $db_field = 'unit_per_price';
                break;
            case 'discount_percent':
            case 'discount percent':
                $db_field = 'discount';
                break;
            case 'stock':
            case 'stock value':
                $db_field = 'stock';
                break;
            case 're-order stock':
            case 're order stock':
                $db_field = 'reorder';
                break;
            case 'brand_name':
            case 'brand name':
                $db_field = 'brand_id';
                break;
            case 'image_primary':
            case 'image primary':
                $db_field = 'primary_image';
                break;
            case 'image_multiple':
            case 'image multiple':
                $db_field = 'multi_image';
                break;
            case 'pack_size':
            case 'pack size':
                $db_field = 'pack';
                break;
            case 'unit_weight':
            case 'unit weight':
                $db_field = 'unit_weight';
                break;
            case 'product_description':
            case 'product description':
                $db_field = 'description';
                break;
            case 'meta title':
            case 'meta_title':
                $db_field = 'meta_title';
                break;
            case 'meta description':
            case 'meta_description':
                $db_field = 'meta_description';
                break;

                // case 'category_1':
                // case 'category 1':
                //     $db_field = 'category_1';
                //     break;
            case 'category':
            case 'child category':
                $db_field = 'category_2';
                break;
            default:
                $db_field = '';
                break;
        }
        return $db_field;
